import React from "react";
import axios from "axios";

const SingleFollowingComponent = ({data, currentUserId, fetchFollowingList}) => {
  const followingUserId = data.id;
  const unfollow = (() => {
    if(data){
      try {
        axios.delete(`${process.env.REACT_APP_BACKEND_URL}0/api/users/${followingUserId}/deleteFollower/${currentUserId}`)
        .then(() => {
          fetchFollowingList();
        }).catch(() => {
          console.error();
        })
      } catch (error) {
        console.log(error);
      }
    }
  })
  const handleOnClick = (() => {
    unfollow();
  });

  return (
    <div>
      <div className="followerContainer">
        <div className="followerContainerImg" style={{float:"right"}}>
          <img src="data:image/png;base64,"></img>
        </div>
        <div style={{flex:"2", textAlign: "center"}}>
          <h1>{data.name}</h1>
        </div>
        <div style={{flex:"3", textAlign: "center"}}>
          <h2>Likes Number</h2>
        </div>
        <div>
          <button onClick={handleOnClick}>Unfollow</button>
        </div>
      </div>
    </div>
  );
};

export default SingleFollowingComponent;